import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [file, setFile] = useState(null);
  const [key, setKey] = useState("");
  const [method, setMethod] = useState("AES");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [downloadUrl, setDownloadUrl] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMessage("");
    setKey("");
    setDownloadUrl("");
  };

  // Encrypt file
  const handleEncrypt = async () => {
    if (!file) return setMessage("Please upload a file first!");
    setLoading(true);
    setMessage("");

    const formData = new FormData();
    formData.append("file", file);
    formData.append("method", method);

    try {
      const response = await axios.post("http://127.0.0.1:5000/encrypt", formData, {
        responseType: "blob",
      });

      // Extract key from headers or text response if backend returns JSON
      const returnedKey = response.headers["x-key"];
      if (!returnedKey) {
        // Try to parse key from JSON text if server sends a blob with metadata
        const text = await response.data.text?.();
        const match = text && text.match(/"key"\s*:\s*"([^"]+)"/);
        setKey(match ? match[1] : "Key not received");
      } else {
        setKey(returnedKey);
      }

      // Save encrypted file
      const encryptedFile = new Blob([response.data], { type: "application/octet-stream" });
      const url = URL.createObjectURL(encryptedFile);
      const filename = file.name + "_encrypted.bin";
      setDownloadUrl(url);

      const a = document.createElement("a");
      a.href = url;
      a.download = filename;
      a.click();

      setMessage(`‚úÖ Encrypted successfully using ${method}`);
    } catch (err) {
      console.error(err);
      setMessage("‚ùå Encryption failed. Check backend logs.");
    } finally {
      setLoading(false);
    }
  };

  // Decrypt file
  const handleDecrypt = async () => {
    if (!file) return setMessage("Please upload a file first!");
    if (!key) return setMessage("Please enter or paste the key!");
    setLoading(true);
    setMessage("");

    const formData = new FormData();
    formData.append("file", file);
    formData.append("key", key);
    formData.append("method", method);

    try {
      const response = await axios.post("http://127.0.0.1:5000/decrypt", formData, {
        responseType: "blob",
      });

      const decryptedFile = new Blob([response.data]);
      const url = URL.createObjectURL(decryptedFile);
      const filename = file.name.replace("_encrypted.bin", "_decrypted");

      const a = document.createElement("a");
      a.href = url;
      a.download = filename;
      a.click();

      setMessage("‚úÖ File decrypted successfully!");
    } catch (err) {
      console.error(err);
      setMessage("‚ùå Decryption failed. Check backend logs.");
    } finally {
      setLoading(false);
    }
  };

  // Copy key to clipboard
  const handleCopyKey = () => {
    if (navigator.clipboard && key) {
      navigator.clipboard.writeText(key);
      setMessage("üîë Key copied to clipboard!");
    }
  };

  // Download key as .txt
  const handleDownloadKey = () => {
    if (!key) return;
    const blob = new Blob([key], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${method}_key.txt`;
    a.click();
  };

  return (
    <div className="app">
      <h1>üîê SecureScan Encryption Suite</h1>

      <div className="card">
        <label className="label">Encryption Method:</label>
        <select
          className="dropdown"
          value={method}
          onChange={(e) => setMethod(e.target.value)}
        >
          <option value="AES">AES (Fast & Secure)</option>
          <option value="Fernet">Fernet (Key-based)</option>
          <option value="RSA">RSA (Public/Private)</option>
        </select>

        <input type="file" onChange={handleFileChange} className="file-input" />

        <div className="button-group">
          <button onClick={handleEncrypt} disabled={loading} className="encrypt-btn">
            {loading ? "Encrypting..." : "Encrypt File"}
          </button>
          <button onClick={handleDecrypt} disabled={loading} className="decrypt-btn">
            {loading ? "Decrypting..." : "Decrypt File"}
          </button>
        </div>

        {key && (
          <div className="key-section">
            <p><b>Generated Key:</b></p>
            <textarea readOnly value={key} className="key-box" />
            <div className="key-actions">
              <button onClick={handleCopyKey} className="copy-btn">Copy Key</button>
              <button onClick={handleDownloadKey} className="download-btn">Download Key</button>
            </div>
          </div>
        )}

        {message && <p className="message">{message}</p>}
      </div>
    </div>
  );
}

export default App;
