import React, { useState } from "react";
import "./App.css";

const API_URL = "http://127.0.0.1:5000";

function App() {
  const [file, setFile] = useState(null);
  const [method, setMethod] = useState("AES");
  const [key, setKey] = useState("");
  const [loading, setLoading] = useState(false);

  const handleEncrypt = async () => {
    if (!file) return alert("Please upload a file.");
    setLoading(true);
    const formData = new FormData();
    formData.append("file", file);
    formData.append("method", method);

    try {
      const response = await fetch(`${API_URL}/encrypt`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Encryption failed");

      const blob = await response.blob();
      const keyHeader = response.headers.get("X-Key");

      if (!keyHeader) {
        alert("No encryption key received.");
        return;
      }

      setKey(keyHeader);
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${file.name}_encrypted.bin`;
      a.click();
    } catch (error) {
      alert("Encryption failed ‚Äî check backend.");
    } finally {
      setLoading(false);
    }
  };

  const handleDecrypt = async () => {
    if (!file || !key) return alert("Please upload a file and enter a key.");
    setLoading(true);

    const formData = new FormData();
    formData.append("file", file);
    formData.append("key", key);
    formData.append("method", method);

    try {
      const response = await fetch(`${API_URL}/decrypt`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Decryption failed");
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${file.name}_decrypted`;
      a.click();
    } catch (error) {
      alert("Decryption failed.");
    } finally {
      setLoading(false);
    }
  };

  const copyKey = () => {
    if (!key) return;
    navigator.clipboard.writeText(key);
    alert("Encryption key copied to clipboard!");
  };

  return (
    <div className="app-container">
      <h1>üîê SecureScan Encryption Suite</h1>
      <div className="card">
        <div className="file-section">
          <input type="file" onChange={(e) => setFile(e.target.files[0])} />
          <select value={method} onChange={(e) => setMethod(e.target.value)}>
            <option value="AES">AES</option>
            <option value="Fernet">Fernet</option>
            <option value="RSA">RSA</option>
          </select>
        </div>

        <div className="button-section">
          <button onClick={handleEncrypt} disabled={loading}>
            {loading ? "Encrypting..." : "Encrypt File"}
          </button>
          <button onClick={handleDecrypt} disabled={loading}>
            {loading ? "Decrypting..." : "Decrypt File"}
          </button>
        </div>

        {key && (
          <div className="key-display">
            <p><strong>Generated Key:</strong></p>
            <div className="key-box">
              <code>{key}</code>
              <button onClick={copyKey}>Copy</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
