import React, { useState } from "react";
import "./App.css";

function App() {
  const [file, setFile] = useState(null);
  const [key, setKey] = useState("");
  const [method, setMethod] = useState("AES");
  const [loading, setLoading] = useState(false);
  const [showKey, setShowKey] = useState(false);
  const [decryptionKey, setDecryptionKey] = useState("");

  const handleFileChange = (e) => setFile(e.target.files[0]);

  // üßæ Function to create a key file with timestamp + method
  const downloadKeyFile = (keyString) => {
    const now = new Date();
    const timestamp = now
      .toISOString()
      .replace(/[-T:]/g, "")
      .split(".")[0]; // 20251101_184500 format
    const fileName = `${method}_key_${timestamp}.key`;

    const blob = new Blob([keyString], { type: "text/plain" });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = fileName;
    document.body.appendChild(link);
    link.click();
    link.remove();
  };

  const handleEncrypt = async () => {
    if (!file) return alert("Please select a file to encrypt.");

    setLoading(true);
    const formData = new FormData();
    formData.append("file", file);
    formData.append("method", method);

    try {
      const response = await fetch("http://127.0.0.1:5000/encrypt", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Encryption failed");

      const encryptionKey = response.headers.get("X-Encryption-Key");
      if (!encryptionKey) throw new Error("No encryption key received.");

      setKey(encryptionKey);
      setShowKey(true);
      downloadKeyFile(encryptionKey);

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.download = `${file.name}.encrypted`;
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      alert(error.message);
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleDecrypt = async () => {
    if (!file) return alert("Please select a file to decrypt.");
    if (!decryptionKey) return alert("Please enter the decryption key.");

    setLoading(true);
    const formData = new FormData();
    formData.append("file", file);
    formData.append("key", decryptionKey);
    formData.append("method", method);

    try {
      const response = await fetch("http://127.0.0.1:5000/decrypt", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Decryption failed");

      const blob = await response.blob();
      const originalName = file.name.replace(".encrypted", "");
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.download = originalName;
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      alert(error.message);
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleCopyKey = async () => {
    try {
      await navigator.clipboard.writeText(key);
      alert("üîë Encryption key copied to clipboard!");
    } catch {
      alert("Failed to copy key.");
    }
  };

  return (
    <div className="app-container">
      <h1>üîê SecureScan Encryption Suite</h1>

      <div className="card">
        <h2>Select Encryption Method</h2>
        <select
          className="dropdown"
          value={method}
          onChange={(e) => setMethod(e.target.value)}
        >
          <option value="AES">AES (Advanced Encryption)</option>
          <option value="Fernet">Fernet (Symmetric)</option>
          <option value="RSA">RSA (Asymmetric)</option>
        </select>

        <input type="file" onChange={handleFileChange} className="file-input" />

        <div className="button-group">
          <button onClick={handleEncrypt} disabled={loading}>
            {loading ? "Encrypting..." : "Encrypt File"}
          </button>
          <button onClick={handleDecrypt} disabled={loading}>
            {loading ? "Decrypting..." : "Decrypt File"}
          </button>
        </div>

        {showKey && (
          <div className="key-section">
            <p>üîë Your Encryption Key:</p>
            <div className="key-box">
              <input type="text" value={key} readOnly />
              <button onClick={handleCopyKey}>Copy</button>
            </div>
            <small>
              Key file downloaded automatically as{" "}
              <strong>{method}_key_YYYYMMDD_HHMM.key</strong>
            </small>
          </div>
        )}

        <div className="key-section">
          <p>Enter Decryption Key:</p>
          <input
            type="text"
            placeholder="Paste your decryption key here"
            value={decryptionKey}
            onChange={(e) => setDecryptionKey(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
