// frontend/src/App.jsx
import React, { useState } from "react";
import "./App.css";

const API_BASE = "http://127.0.0.1:5000";

function timestampString() {
  const now = new Date();
  return now.toISOString().replace(/[:.-]/g, "").slice(0, 15); // e.g. 20251101T184500
}

export default function App() {
  const [file, setFile] = useState(null);
  const [method, setMethod] = useState("AES");
  const [key, setKey] = useState("");
  const [decryptionKey, setDecryptionKey] = useState("");
  const [showKey, setShowKey] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (ev) => {
    setFile(ev.target.files[0]);
  };

  const downloadBlob = (blob, filename) => {
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    a.remove();
    URL.revokeObjectURL(url);
  };

  const saveKeyFile = (keyString) => {
    const ts = timestampString();
    const fileName = `${method}_key_${ts}.key`;
    const blob = new Blob([keyString], { type: "text/plain" });
    downloadBlob(blob, fileName);
  };

  const handleEncrypt = async () => {
    if (!file) {
      alert("Choose a file first.");
      return;
    }
    setLoading(true);
    try {
      const fd = new FormData();
      fd.append("file", file);
      fd.append("method", method);

      const res = await fetch(`${API_BASE}/encrypt`, {
        method: "POST",
        body: fd,
      });

      if (!res.ok) {
        const txt = await res.text();
        throw new Error(txt || "Encryption failed");
      }

      // parse JSON (filename, key) ‚Äî server returns JSON
      const json = await res.json().catch(() => null);

      // header may include key too ‚Äî read both ways
      const headerKey =
        res.headers.get("X-Encryption-Key") || res.headers.get("x-encryption-key");
      const returnedKey = headerKey || (json && json.key);

      if (!returnedKey) {
        throw new Error("No encryption key received from server.");
      }

      setKey(returnedKey);
      setShowKey(true);
      // auto-download key file
      saveKeyFile(returnedKey);

      // determine filename (from JSON) and download encrypted file
      const filename = (json && json.filename) ? json.filename : (file.name + ".encrypted");

      // download encrypted file via GET endpoint
      const fileRes = await fetch(`${API_BASE}/download/${encodeURIComponent(filename)}`);
      if (!fileRes.ok) throw new Error("Failed to download encrypted file.");
      const blob = await fileRes.blob();
      downloadBlob(blob, filename);

      alert("Encryption successful ‚Äî key received and downloads started.");
    } catch (err) {
      console.error(err);
      alert("Encryption error: " + (err.message || err));
    } finally {
      setLoading(false);
    }
  };

  const handleDecrypt = async () => {
    if (!file) {
      alert("Choose an encrypted file to decrypt.");
      return;
    }
    const keyToUse = decryptionKey || key;
    if (!keyToUse) {
      alert("Please paste the decryption key or use the recently generated key.");
      return;
    }

    setLoading(true);
    try {
      const fd = new FormData();
      fd.append("file", file);
      fd.append("method", method);
      fd.append("key", keyToUse);

      const res = await fetch(`${API_BASE}/decrypt`, {
        method: "POST",
        body: fd,
      });

      if (!res.ok) {
        const txt = await res.text();
        throw new Error(txt || "Decryption failed");
      }

      const disposition = res.headers.get("Content-Disposition") || "";
      const match = disposition.match(/filename="?([^"]+)"?/);
      const filename = match ? match[1] : file.name.replace(".encrypted", "decrypted");

      const blob = await res.blob();
      downloadBlob(blob, filename);
      alert("Decryption successful ‚Äî file downloaded.");
    } catch (err) {
      console.error(err);
      alert("Decryption error: " + (err.message || err));
    } finally {
      setLoading(false);
    }
  };

  const handleCopyKey = async () => {
    if (!key) return alert("No key to copy.");
    try {
      await navigator.clipboard.writeText(key);
      alert("Key copied to clipboard.");
    } catch {
      alert("Failed to copy key to clipboard.");
    }
  };

  return (
    <div className="app-container">
      <h1>üîê SecureScan Encryption Suite</h1>
      <div className="card">
        <label>Encryption Method</label>
        <select value={method} onChange={(e) => setMethod(e.target.value)}>
          <option value="AES">AES (AES-GCM, symmetric)</option>
          <option value="Fernet">Fernet (cryptography.Fernet)</option>
          <option value="RSA">RSA (hybrid RSA+AES)</option>
        </select>

        <label>Choose file</label>
        <input type="file" onChange={handleFileChange} />

        <div className="button-row">
          <button onClick={handleEncrypt} disabled={loading}>
            {loading ? "Working..." : "Encrypt File"}
          </button>
          <button onClick={handleDecrypt} disabled={loading}>
            {loading ? "Working..." : "Decrypt File"}
          </button>
        </div>

        {showKey && (
          <div className="key-area">
            <label>Encryption key (received)</label>
            <div className="key-row">
              <input type="text" value={key} readOnly />
              <button onClick={handleCopyKey}>Copy</button>
            </div>
          </div>
        )}

        <div className="key-area">
          <label>Or paste Decryption Key</label>
          <input
            type="text"
            placeholder="Paste key here to decrypt"
            value={decryptionKey}
            onChange={(e) => setDecryptionKey(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
}
